import React, { useState, useEffect } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/Layout.css';

// Логирование запросов (оставляем как есть)
axios.interceptors.request.use((config) => {
  console.log('Request Headers:', config.headers);
  console.log('Cookies being sent:', document.cookie);
  return config;
}, (error) => {
  console.error('Request error:', error);
  return Promise.reject(error);
});

// Логирование ответов (оставляем как есть)
axios.interceptors.response.use((response) => {
  console.log('Response Headers:', response.headers);
  console.log('Response Data:', response.data);
  return response;
}, (error) => {
  console.error('Response error:', error.response?.data || error.message);
  return Promise.reject(error);
});

const Layout = () => {
  const [currentUser, setCurrentUser] = useState(undefined); // Инициализируем как undefined для состояния загрузки
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // useNavigate все еще может быть полезен для других навигаций

  // Function to check authentication and fetch user data
  const fetchCurrentUser = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/protected', {
        withCredentials: true, // Include cookies in the request
      });
      setCurrentUser(response.data.user);
    } catch (error) {
      if (error.response && (error.response.status === 401 || error.response.status === 403)) {
        setCurrentUser(null); // User is not authorized
      } else {
        console.error('Error fetching current user:', error);
        setCurrentUser(null); // В случае других ошибок также считаем пользователя неавторизованным
      }
    } finally {
      setLoading(false);
    }
  };

  // Function to handle logout
  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true });
      // После успешного выхода на сервере, принудительно перезагружаем страницу
      // и перенаправляем на страницу авторизации.
      // Это сбросит все состояние React и обеспечит "чистый" переход.
      window.location.href = '/auth';
    } catch (error) {
      console.error('Error during logout:', error);
      // Даже если произошла ошибка при выходе на сервере,
      // все равно пытаемся перенаправить пользователя на страницу логина,
      // так как локальное состояние может быть уже невалидным.
      alert('Произошла ошибка при выходе из системы на сервере. Перенаправление на страницу входа.');
      window.location.href = '/auth';
    }
  };

  useEffect(() => {
    fetchCurrentUser();
  }, []);

  // Можно отображать глобальный загрузчик, пока currentUser === undefined
  if (currentUser === undefined || loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <p>Загрузка информации о пользователе...</p>
        {/* Можно добавить спиннер */}
      </div>
    );
  }

  return (
    <div className="layout-container">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <Link to={currentUser ? "/home" : "/auth"} className="logo-link"> {/* Динамическая ссылка для лого */}
            <h1 className="logo">MyApp</h1>
          </Link>
        </div>
        <div className="header-right">
          {currentUser ? (
            <div className="user-info">
              <span className="user-name">Добро пожаловать, {currentUser.name}</span>
              <Link to="/profile" className="profile-button">
                Профиль
              </Link>
              <button onClick={handleLogout} className="logout-button">
                Выход
              </button>
            </div>
          ) : (
            <Link to="/auth" className="login-link">
              Вход
            </Link>
          )}
        </div>
      </header>

      {/* Main Content Area */}
      <div className="main-content">
        {currentUser && ( // Сайдбар отображается только для авторизованных пользователей
          <aside className="sidebar">
            <nav className="nav-menu">
              <ul>
                <li>
                  <Link to="/home" className="nav-link">
                    <i className="fas fa-home"></i> Главная
                  </Link>
                </li>
                <li>
                  <Link to="/profile" className="nav-link">
                    <i className="fas fa-user"></i> Профиль
                  </Link>
                </li>
                <li>
                  <Link to="/settings" className="nav-link">
                    <i className="fas fa-cog"></i> Настройки
                  </Link>
                </li>
              </ul>
            </nav>
          </aside>
        )}

        {/* Outlet будет рендерить либо страницу входа/регистрации, либо защищенный контент */}
        {/* Класс 'full-width' применяется, если сайдбара нет (пользователь не авторизован) */}
        <main className={`content-area ${currentUser ? 'with-sidebar' : 'full-width'}`}>
          <Outlet context={{ currentUser, setCurrentUser }} /> {/* Передаем currentUser и setCurrentUser в Outlet, если нужно */}
        </main>
      </div>
    </div>
  );
};

export default Layout;
