import React, { useState, useEffect } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/Layout.css';

const Layout = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Функция для проверки аутентификации и получения данных пользователя
  const fetchCurrentUser = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/protected', {
        withCredentials: true, // Включаем отправку куки
      });
      setCurrentUser(response.data.user); // Предполагаем, что сервер возвращает данные пользователя
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setCurrentUser(null); // Пользователь не авторизован
      }
    } finally {
      setLoading(false);
    }
  };

  // Функция для выхода из системы
  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true }); // Отправляем запрос на выход
      setCurrentUser(null); // Очищаем данные пользователя
      navigate('/auth', { replace: true }); // Перенаправляем на страницу авторизации
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  useEffect(() => {
    fetchCurrentUser(); // Проверяем аутентификацию при загрузке компонента
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Индикатор загрузки
  }

  return (
    <div className="layout-container">
      {/* Шапка */}
      <header className="header">
        <div className="header-left">
          <h1 className="logo">MyApp</h1>
        </div>
        <div className="header-right">
          {currentUser ? (
            <div className="user-info">
              <span className="user-email">{currentUser.email || currentUser.name}</span>
              <button onClick={handleLogout} className="logout-button">
                Выйти
              </button>
            </div>
          ) : (
            <Link to="/auth" className="login-link">
              Войти
            </Link>
          )}
        </div>
      </header>

      {/* Основной контент */}
      <div className="main-content">
        {/* Боковое меню */}
        <aside className="sidebar">
          <nav className="nav-menu">
            <ul>
              <li>
                <Link to="/home" className="nav-link">
                  <i className="fas fa-home"></i> Главная
                </Link>
              </li>
              <li>
                <Link to="/profile" className="nav-link">
                  <i className="fas fa-user"></i> Профиль
                </Link>
              </li>
              <li>
                <Link to="/settings" className="nav-link">
                  <i className="fas fa-cog"></i> Настройки
                </Link>
              </li>
              {/* Добавьте другие пункты меню по необходимости */}
            </ul>
          </nav>
        </aside>

        {/* Область контента */}
        <main className="content-area">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
