import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../css/Home.css";

const Home = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const loadData = async () => {
            try {
                await new Promise((resolve) => setTimeout(resolve, 1000));
                const response = await axios.get("http://localhost:5000/api/items");
                setData(response.data);
                setLoading(false);
            } catch (error) {
                if (error.response) {
                    setError(error.response.data.message || "An error occurred while loading data.");
                } else if (error.request) {
                    setError("No response from the server. Please try again later.");
                } else {
                    setError("An unexpected error occurred.");
                }
                setLoading(false);
            }
        };

        loadData();
    }, []);

    return (
        <div className="home-container">
            <div className="home-header">
                <h1 className="home-title">Device List</h1>
                <Link to="/add" className="add-device-button">
                    Add Device
                </Link>
            </div>

            {loading ? (
                <div className="loading-state">
                    <div className="spinner"></div>
                    <p>Loading devices...</p>
                </div>
            ) : error ? (
                <div className="error-state">
                    <p>{error}</p>
                </div>
            ) : data.length === 0 ? (
                <div className="empty-state">
                    <p>No devices found. Add your first device!</p>
                </div>
            ) : (
                <div className="device-list">
                    {data.map((item) => (
                        <Link 
                            to={`/detail/${item.id}`} 
                            className="device-card" 
                            key={item.id}
                        >
                            <div className="device-icon">
                                {/* Используем Unicode символ */}
                                <span className="icon-circle">&#9679;</span>
                            </div>
                            <div className="device-info">
                                <h3>{item.name}</h3>
                                <p className="device-type">{item.type || 'Unknown type'}</p>
                                <p className="device-status">
                                    <span className={`status-indicator ${item.status || 'inactive'}`}>
                                        {item.status || 'Inactive'}
                                    </span>
                                </p>
                            </div>
                        </Link>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Home;
