import React from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import '../css/Layout.css';

const Layout = () => {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // Вызываем функцию logout из useAuth
    navigate('/auth', { replace: true }); // Перенаправляем на страницу авторизации
  };

  return (
    <div className="layout-container">
      {/* Шапка */}
      <header className="header">
        <div className="header-left">
          <h1 className="logo">MyApp</h1>
        </div>
        <div className="header-right">
          {currentUser && (
            <div className="user-info">
              <span className="user-email">{currentUser.email}</span>
              <button onClick={handleLogout} className="logout-button">
                Выйти
              </button>
            </div>
          )}
        </div>
      </header>

      {/* Основной контент */}
      <div className="main-content">
        {/* Боковое меню */}
        <aside className="sidebar">
          <nav className="nav-menu">
            <ul>
              <li>
                <Link to="/home" className="nav-link">
                  <i className="fas fa-home"></i> Главная
                </Link>
              </li>
              <li>
                <Link to="/profile" className="nav-link">
                  <i className="fas fa-user"></i> Профиль
                </Link>
              </li>
              <li>
                <Link to="/settings" className="nav-link">
                  <i className="fas fa-cog"></i> Настройки
                </Link>
              </li>
              {/* Добавьте другие пункты меню по необходимости */}
            </ul>
          </nav>
        </aside>

        {/* Область контента */}
        <main className="content-area">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
