import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import '../css/Form.css';

const Form = () => {
  // Init state для управления полями формы
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [type, setType] = useState('');
  const navigate = useNavigate();

  // Обновление состояния для полей name, location и type
  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleTypeChange = (e) => {
    setType(e.target.value);
  };

  // Обработка отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();

    const deviceData = {
      name,
      location,
      type,
      status: 'Inactive',
    };

    axios
      .post('http://localhost:5000/api/items', deviceData)
      .then(() => {
        console.log("Device data successfully sent to server");
        navigate('/home');
      })
      .catch((error) => {
        console.error("Error sending device data to server: ", error);
      });
  };

  // Функция для кнопки "Назад на главную страницу"
  const handleGoHome = () => {
    navigate('/home');
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Add New Device</h2>
      <form onSubmit={handleSubmit} className="form">
        {/* Поле для имени устройства */}
        <div className="form-group">
          <label className="form-label">
            Device Name:
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              required
              className="form-input"
            />
          </label>
        </div>

        {/* Поле для расположения устройства */}
        <div className="form-group">
          <label className="form-label">
            Location:
            <input
              type="text"
              value={location}
              onChange={handleLocationChange}
              required
              className="form-input"
            />
          </label>
        </div>

        {/* Выпадающий список для выбора типа устройства */}
        <div className="form-group">
          <label className="form-label">
            Device Type:
            <select
              value={type}
              onChange={handleTypeChange}
              required
              className="form-input"
            >
              <option value="" disabled>Select a type</option>
              <option value="1">Detector</option>
              <option value="2">Camera</option>
              <option value="Other">Other</option>
            </select>
          </label>
        </div>

        {/* Контейнер для кнопок */}
        <div className="form-actions">
          <button type="submit" className="form-button">
            Save Data
          </button>
          <button
            type="button" // Важно: type="button", чтобы не сабмитить форму
            className="form-button" // Используем тот же класс для идентичного стиля
            onClick={handleGoHome} // Обработчик для перехода на главную
          >
            Back to Main
          </button>
        </div>
      </form>
    </div>
  );
};

export default Form;
