import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import '../css/Form.css';

const Form = () => {
  // Init state для управления полями формы
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const navigate = useNavigate();

  // Обновление состояния для полей name и location
  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  // Обработка отправки формы
  const handleSubmit = (e) => {
    e.preventDefault(); // Отключение стандартного поведения формы

    // Добавляем статус "Inactive" к данным устройства
    const deviceData = {
      name,
      location,
      status: 'Inactive', // Устанавливаем статус "Inactive"
    };

    // Отправка данных на сервер
    axios
      .post('http://localhost:5000/api/items', deviceData)
      .then(() => {
        console.log("Device data successfully sent to server");
        navigate('/home'); // Перенаправление на главную страницу
      })
      .catch((error) => {
        console.error("Error sending device data to server: ", error);
      });
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Add New Device</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label className="form-label">
            Device Name:
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              required
              className="form-input"
            />
          </label>
        </div>

        <div className="form-group">
          <label className="form-label">
            Location:
            <input
              type="text"
              value={location}
              onChange={handleLocationChange}
              required
              className="form-input"
            />
          </label>
        </div>

        <button type="submit" className="form-button">
          Save Data
        </button>
      </form>
    </div>
  );
};

export default Form;
