import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/AuthPage.css';

const AuthPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        name: username,
        password
      });

      // Создаем экземпляр axios с токеном для последующих запросов
      const authenticatedAxios = axios.create({
        baseURL: 'http://localhost:5000',
        headers: {
          Authorization: `Bearer ${response.data.token}`
        }
      });

      // Сохраняем экземпляр axios в контексте или глобально
      // Например, можно использовать React Context или Redux
      setAuthAxios(authenticatedAxios);

      navigate('/home', { replace: true });
    } catch (err) {
      setError(err.response?.data?.message || 'Произошла ошибка при авторизации');
    } finally {
      setIsLoading(false);
    }
  };


    return (
      <div className="auth-container">
        <div className="auth-card">
          <h1 className="auth-title">Авторизация</h1>

          {error && <div className="auth-error">{error}</div>}

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label htmlFor="username">Логин</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                placeholder="Введите ваш логин"
                autoComplete="username"
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Пароль</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="Введите ваш пароль"
                autoComplete="current-password"
              />
            </div>

            <div className="form-options">
              <label className="checkbox-container">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                />
                <span className="checkmark"></span>
                Запомнить меня
              </label>

              <a href="/forgot-password" className="forgot-password">
                Забыли пароль?
              </a>
            </div>

            <button
              type="submit"
              className="auth-button"
              disabled={isLoading}
            >
              {isLoading ? 'Вход...' : 'Войти'}
            </button>
          </form>

          <div className="auth-footer">
            Нет аккаунта? <a href="/register">Зарегистрироваться</a>
          </div>
        </div>
      </div>
    );
  };

  export default AuthPage;
