import React, { useState, useEffect } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/Layout.css'; // Make sure this path is correct

// Логирование запросов (оставляем как есть)
axios.interceptors.request.use((config) => {
  console.log('Request Headers:', config.headers);
  console.log('Cookies being sent:', document.cookie);
  return config;
}, (error) => {
  console.error('Request error:', error);
  return Promise.reject(error);
});

// Логирование ответов (оставляем как есть)
axios.interceptors.response.use((response) => {
  console.log('Response Headers:', response.headers);
  console.log('Response Data:', response.data);
  return response;
}, (error) => {
  console.error('Response error:', error.response?.data || error.message);
  return Promise.reject(error);
});

const Layout = () => {
  const [currentUser, setCurrentUser] = useState(undefined);
  const [loading, setLoading] = useState(true);
  // const navigate = useNavigate(); // useNavigate can still be useful

  const fetchCurrentUser = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/protected', {
        withCredentials: true,
      });
      setCurrentUser(response.data.user);
    } catch (error) {
      if (error.response && (error.response.status === 401 || error.response.status === 403)) {
        setCurrentUser(null);
      } else {
        console.error('Error fetching current user:', error);
        setCurrentUser(null);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true });
      // Force a hard redirect to /auth. This clears React state and re-initializes the app.
      // On the /auth page, fetchCurrentUser will likely result in currentUser being null.
      window.location.href = '/auth';
    } catch (error) {
      console.error('Error during logout:', error);
      alert('Произошла ошибка при выходе из системы на сервере. Перенаправление на страницу входа.');
      window.location.href = '/auth'; // Still redirect
    }
  };

  useEffect(() => {
    fetchCurrentUser();
  }, []);

  if (loading) { // Simplified loading check
    return (
      <div className="loading-container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <p>Загрузка информации о пользователе...</p>
        {/* Можно добавить спиннер */}
      </div>
    );
  }

  return (
    <div className="layout-container">
      <header className="header">
        <div className="header-left">
          <Link to={currentUser ? "/home" : "/auth"} className="logo-link">
            <h1 className="logo">MyApp</h1>
          </Link>
        </div>
        <div className="header-right">
          {currentUser ? (
            <div className="user-info">
              <span className="user-name">Добро пожаловать, {currentUser.name}</span>
              <Link to="/profile" className="profile-button">
                Профиль
              </Link>
              <button onClick={handleLogout} className="logout-button">
                Выход
              </button>
            </div>
          ) : (
            <Link to="/auth" className="login-link">
              Вход
            </Link>
          )}
        </div>
      </header>

      <div className="main-content-wrapper"> {/* Added a wrapper for flex layout */}
        {currentUser && (
          <aside className="sidebar">
            <nav className="nav-menu">
              <ul>
                <li>
                  <Link to="/home" className="nav-link">
                    <i className="fas fa-home"></i> Главная
                  </Link>
                </li>
                <li>
                  <Link to="/profile" className="nav-link">
                    <i className="fas fa-user"></i> Профиль
                  </Link>
                </li>
                <li>
                  <Link to="/settings" className="nav-link">
                    <i className="fas fa-cog"></i> Настройки
                  </Link>
                </li>
                {/* Add more links as needed */}
              </ul>
            </nav>
          </aside>
        )}
        <main className={`content-area ${!currentUser ? 'full-width' : ''}`}>
          {/* The Outlet context might be useful if child routes need to trigger a re-fetch or modify currentUser */}
          <Outlet context={{ currentUser, setCurrentUser, fetchCurrentUser }} />
        </main>
      </div>
    </div>
  );
};

export default Layout;
