// Layout.js
import React, { useState, useEffect } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/Layout.css';

// Accept handleLogout prop from App.js
const Layout = ({ handleLogout: notifyAppLogout }) => {
  const [currentUser, setCurrentUser] = useState(null);
  // Loading state specific to fetching user info for the layout display
  const [isLoadingUserInfo, setIsLoadingUserInfo] = useState(true);
  const navigate = useNavigate();

  // This function now primarily fetches user details for display *if* authenticated
  const fetchCurrentUserDetails = async () => {
    setIsLoadingUserInfo(true);
    try {
      const response = await axios.get('http://localhost:5000/api/protected', {
        withCredentials: true,
      });
      setCurrentUser(response.data.user); // Set user details for display
    } catch (error) {
      // If this fails (e.g., token expired), the ProtectedRoute should handle redirect.
      // We can clear local user info.
      console.error('Error fetching user details for layout:', error);
      setCurrentUser(null);
      // Optional: If error is 401/403 here, trigger the main logout flow
      if (error.response && (error.response.status === 401 || error.response.status === 403)) {
           // Use the logout handler passed from App to ensure global state is updated
           // This handles cases where the session expires while using the app
           await handleLogoutClick();
      }
    } finally {
      setIsLoadingUserInfo(false);
    }
  };

  // Call this when the component mounts or when needed to refresh user display
  useEffect(() => {
    fetchCurrentUserDetails();
     // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Centralized logout logic
  const handleLogoutClick = async () => {
    try {
      await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true });
      // Clear local user state FIRST
      setCurrentUser(null);
      // Notify App.js that logout happened SECOND
      if (notifyAppLogout) {
         notifyAppLogout(); // This sets isAuthenticated in App.js to false
      }
      // Navigate AFTER state updates
      navigate('/auth', { replace: true });
    } catch (error) {
      console.error('Error during logout:', error);
      // Still attempt to clear state and navigate even if API call fails
      setCurrentUser(null);
      if (notifyAppLogout) {
         notifyAppLogout();
      }
      navigate('/auth', { replace: true });
    }
  };

  // Decide whether to show user info or login based on currentUser state
  // which is populated by fetchCurrentUserDetails
  const showUserInfo = !isLoadingUserInfo && currentUser;

  return (
    <div className="layout-container">
      <header className="header">
        <div className="header-left">
          <Link to="/home" className="logo-link"><h1 className="logo">MyApp</h1></Link>
        </div>
        <div className="header-right">
          {isLoadingUserInfo ? (
            <span className="user-info-loading">Loading user...</span>
          ) : showUserInfo ? (
            <div className="user-info">
              <span className="user-name">Welcome, {currentUser.name}</span>
              {/* Use the centralized handler */}
              <button onClick={handleLogoutClick} className="logout-button">
                Logout
              </button>
            </div>
          ) : (
             // Only show Login link if not loading AND no user info found
            <Link to="/auth" className="login-link">
              Login
            </Link>
          )}
        </div>
      </header>

      <div className="main-content">
        {/* Sidebar should only render if user is definitively logged in */}
        {showUserInfo && (
          <aside className="sidebar">
            <nav className="nav-menu">
              <ul>
                <li><Link to="/home" className="nav-link"><i className="fas fa-home"></i> Home</Link></li>
                <li><Link to="/profile" className="nav-link"><i className="fas fa-user"></i> Profile</Link></li>
                <li><Link to="/settings" className="nav-link"><i className="fas fa-cog"></i> Settings</Link></li>
              </ul>
            </nav>
          </aside>
        )}

        <main className={`content-area ${showUserInfo ? 'with-sidebar' : 'full-width'}`}>
          <Outlet /> {/* Renders Home, Profile, Detail etc. */}
        </main>
      </div>
    </div>
  );
};

export default Layout;
