// Profile.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for the back button
import '../css/Profile.css'; // Ensure this CSS file exists and is styled
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faUser } from '@fortawesome/free-solid-svg-icons'; // Import icons

const Profile = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      setLoading(true);
      setError('');
      try {
        const response = await axios.get('http://localhost:5000/api/protected', {
          withCredentials: true,
        });
        if (response.data && response.data.user.name) {
          setCurrentUser(response.data.user);
        } else {
          console.error("User data not found in response:", response.data);
          setError('Could not retrieve user profile information.');
        }
      } catch (err) {
        if (err.response && err.response.status === 401) {
          setError("Your session may have expired. Please log in again.");
          // Consider redirecting to /auth here if appropriate for your app flow
        } else if (err.response) {
          setError(err.response.data.message || 'An error occurred while loading profile data.');
        } else if (err.request) {
          setError('Could not connect to the server to load profile.');
        } else {
          setError('An unexpected error occurred while loading profile.');
        }
        console.error("Error loading profile:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, []);

  const handleGoBack = () => {
    navigate(-1); // Go back to the previous page in history
  };

  if (loading) {
    return (
      <div className="profile-container">
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container error-state">
        <div className="error-content">
          <FontAwesomeIcon icon={faUser} size="2x" className="error-icon" />
          <p className="error-message">{error}</p>
          <button onClick={handleGoBack} className="back-button">
            <FontAwesomeIcon icon={faArrowLeft} className="button-icon" /> Back
          </button>
        </div>
      </div>
    );
  }

  if (!currentUser) {
    return (
      <div className="profile-container error-state">
        <div className="error-content">
          <FontAwesomeIcon icon={faUser} size="2x" className="error-icon" />
          <p className="error-message">Could not display profile information. User data might be missing.</p>
          <button onClick={handleGoBack} className="back-button">
            <FontAwesomeIcon icon={faArrowLeft} className="button-icon" /> Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <button onClick={handleGoBack} className="back-button">
          <FontAwesomeIcon icon={faArrowLeft} className="button-icon" /> Back
        </button>
        <h2><FontAwesomeIcon icon={faUser} className="profile-icon" /> Your Profile</h2>
      </div>
      <div className="profile-details">
        <p><strong>Name:</strong> {currentUser.name}</p>
        <p><strong>ID:</strong> {currentUser.id}</p>
        {/* Add other user details here */}
        {/* Example: <p><strong>Email:</strong> {currentUser.email}</p> */}
      </div>
      {/* You can add more UI elements or information here */}
    </div>
  );
};

export default Profile;
