import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/Home.css";

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [loginError, setLoginError] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  // Функция для проверки аутентификации
  const checkAuthentication = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/protected", {
        withCredentials: true, // Включаем отправку куки
      });
      return !!response.data; // Если данные есть, пользователь авторизован
    } catch (error) {
      if (error.response && error.response.status === 401) {
        return false; // Пользователь не авторизован
      }
      throw error;
    }
  };

  useEffect(() => {
    const loadData = async () => {
      const isAuthenticated = await checkAuthentication();
      if (!isAuthenticated) {
        setLoading(false);
        return;
      }

      try {
        const response = await axios.get("http://localhost:5000/api/items", {
          withCredentials: true, // Включаем отправку куки
        });
        setData(response.data);
        setLoading(false);
      } catch (error) {
        if (error.response) {
          setError(error.response.data.message || "An error occurred while loading data.");
        } else if (error.request) {
          setError("No response from the server. Please try again later.");
        } else {
          setError("An unexpected error occurred.");
        }
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError(""); // Очищаем предыдущие ошибки

    try {
      await axios.post(
        "http://localhost:5000/api/login",
        { name: username, password },
        { withCredentials: true } // Включаем отправку куки
      );
      navigate("/"); // Перенаправляем на главную страницу после входа
    } catch (error) {
      if (error.response) {
        setLoginError(error.response.data.message || "Invalid credentials");
      } else {
        setLoginError("An unexpected error occurred.");
      }
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post("http://localhost:5000/api/logout", {}, { withCredentials: true }); // Отправляем запрос на выход
      navigate("/"); // Перенаправляем на главную страницу
    } catch (error) {
      console.error("Ошибка при выходе:", error);
    }
  };

  const isAuthenticated = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/protected", {
        withCredentials: true,
      });
      return !!response.data;
    } catch (error) {
      return false;
    }
  };

  return (
    <div className="home-container">
      {/* Проверка аутентификации */}
      {async () => {
        const authenticated = await isAuthenticated();
        if (!authenticated) {
          return (
            <div className="login-container">
              <form onSubmit={handleLogin} className="login-form">
                <h2>Login</h2>
                {loginError && <p className="error-message">{loginError}</p>}
                <input
                  type="text"
                  placeholder="Username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <button type="submit">Login</button>
              </form>
            </div>
          );
        }
      }}

      <div className="home-header">
        <h1 className="home-title">Device List</h1>
        <Link to="/add" className="add-device-button">
          Add Device
        </Link>
        <button onClick={handleLogout} className="logout-button">
          Logout
        </button>
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Loading devices...</p>
        </div>
      ) : error ? (
        <div className="error-state">
          <p>{error}</p>
        </div>
      ) : data.length === 0 ? (
        <div className="empty-state">
          <p>No devices found. Add your first device!</p>
        </div>
      ) : (
        <div className="device-list">
          {data.map((item) => (
            <Link
              to={`/detail/${item.id}`}
              className="device-card"
              key={item.id}
            >
              <div className="device-icon">
                {/* Используем Unicode символ */}
                <span className="icon-circle">&#9679;</span>
              </div>
              <div className="device-info">
                <h3>{item.name}</h3>
                <p className="device-type">{item.type || 'Unknown type'}</p>
                <p className="device-status">
                  {/* Динамическое применение классов */}
                  <span
                    className={`status-indicator ${
                      item.status === 'Active' ? 'active' : 'inactive'
                    }`}
                  >
                    {item.status || 'Inactive'}
                  </span>
                </p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
