// Home.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Removed useNavigate
import axios from "axios";
import "../css/Home.css";

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  // Removed useNavigate as logout is handled by Layout

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      setError("");
      try {
        const response = await axios.get("http://localhost:5000/api/items", {
          withCredentials: true,
        });
        setData(response.data);
      } catch (err) {
        // Error handling remains important
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
           // Session might have expired between Layout load and Home load.
           // ProtectedRoute in App.js should ideally catch this, but we can inform the user.
           // Layout's fetchCurrentUserDetails also has logic to trigger logout now.
           setError("Authentication error. You might be logged out automatically.");
           // No need to navigate here, rely on Layout/App state management
        } else {
            const message = err.response?.data?.message || err.message || "An error occurred loading data.";
            setError(message);
        }
        console.error("Error loading data:", err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []); // Run once on mount

  // --- REMOVED handleLogout function ---

  return (
    <div className="home-container">
      <div className="home-header">
        <h1 className="home-title">Device List</h1>
        {/* --- REMOVED Logout button --- */}
        {/* Optional: Add Device link/button if needed */}
        {/* <Link to="/add" className="add-device-button"> Add Device </Link> */}
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Loading devices...</p>
        </div>
      ) : error ? (
        <div className="error-state">
          <p>{error}</p>
          {/* Optional: Button to retry fetching data */}
          {/* <button onClick={loadData}>Retry</button> */}
        </div>
      ) : data.length === 0 ? (
        <div className="empty-state">
          <p>No devices found.</p>
        </div>
      ) : (
        <div className="device-list">
          {data.map((item) => (
            // Make sure the detail route path in App.js matches this ("/detail/:id")
            <Link
              to={`/detail/${item.id}`}
              className="device-card"
              key={item.id}
            >
               <div className="device-icon">
                {/* Simple icon example */}
                <span className={`icon-indicator ${item.status === 'Active' ? 'active' : 'inactive'}`}></span>
              </div>
              <div className="device-info">
                <h3>{item.name}</h3>
                <p className="device-type">{item.type || 'Unknown type'}</p>
                 {/* Removed redundant status display from here if icon/indicator is sufficient */}
                 <p className="device-location">{item.location || 'Unknown location'}</p>
              </div>
               <div className="device-status">
                   <span className={`status-tag ${item.status === 'Active' ? 'active' : 'inactive'}`}>
                       {item.status || 'Unknown'}
                   </span>
               </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
