// Profile.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../css/Profile.css';

const Profile = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Fetch user profile data - assumes user is authenticated
  useEffect(() => {
    const fetchUserProfile = async () => {
      setLoading(true);
      setError('');
      try {
        // This endpoint should return the logged-in user's data
        const response = await axios.get('http://localhost:5000/api/protected', { // Or '/api/user/me' etc.
          withCredentials: true,
        });
        // Ensure the response structure matches: { user: { name: '...', id: '...' } }
        if (response.data && response.data.user) {
            setCurrentUser(response.data.user);
        } else {
            // Handle case where API returns success but no user data
            console.error("User data not found in response:", response.data);
            setError('Could not retrieve user profile information.');
        }
      } catch (err) {
        // Handle errors fetching profile data
        // A 401 here might mean the session expired *after* initial load
         if (err.response && err.response.status === 401) {
           setError("Your session may have expired. Please log out and log back in.");
           // Optionally trigger logout or redirect
         } else if (err.response) {
          setError(err.response.data.message || 'An error occurred while loading profile data.');
        } else if (err.request) {
           setError('Could not connect to the server to load profile.');
        } else {
          setError('An unexpected error occurred while loading profile.');
        }
        console.error("Error loading profile:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, []); // Run once on mount

  // Logout function
  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true });
      // Consider using updateAuthState(false) from App via context/props if available
      navigate('/auth', { replace: true }); // Redirect to login after logout
    } catch (error) {
      console.error('Error during logout:', error);
      // Optionally show an error message
    }
  };

  // --- Rendering Logic ---

  if (loading) {
    return <p>Loading profile...</p>;
  }

  // If there was an error fetching profile data (after being authenticated)
  if (error) {
    return (
        <div className="profile-container error-state">
             <p>{error}</p>
             {/* Optionally add logout button even on error */}
             <button onClick={handleLogout}>Logout</button>
        </div>
    );
  }

  // If loading is done, no error, but currentUser is still null (shouldn't happen if API is correct)
  if (!currentUser) {
     return (
         <div className="profile-container error-state">
             <p>Could not display profile information.</p>
             <button onClick={handleLogout}>Logout</button>
         </div>
     )
  }

  // Successful load
  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p>Name: {currentUser.name}</p>
      <p>ID: {currentUser.id}</p>
      {/* Display other user info as needed */}
      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
};

export default Profile;
