import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

// Моковые данные пользователей (в реальном приложении это будет запрос к API)
const mockUsers = [
  { id: 1, email: 'user', password: '123' },
  { id: 2, email: 'admin', password: '123' }
];

export function useAuth() {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const userData = sessionStorage.getItem('user');
    setIsAuthenticated(!!userData);
  }, []);

  const login = async (username, password) => {
    try {
      // Здесь должен быть реальный запрос к API
      const user = mockUsers.find(u => 
        u.username === username && u.password === password
      );

      if (user) {
        sessionStorage.setItem('user', JSON.stringify(user));
        setIsAuthenticated(true); // Важно обновить состояние!
        return { success: true };
      }
      return { success: false, error: 'Неверный логин или пароль' };
    } catch (error) {
      return { success: false, error: 'Ошибка сервера' };
    }
  };

  const logout = () => {
    sessionStorage.removeItem('user');
    setCurrentUser(null);
    setIsAuthenticated(false);
    navigate('/auth');
  };

  return { 
    isAuthenticated, 
    currentUser, 
    login, 
    logout 
  };
}
