import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../css/Profile.css';

const Profile = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Функция для получения данных пользователя
  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/protected', {
        withCredentials: true, // Включаем отправку куки
      });
      setCurrentUser(response.data.user); // Предполагаем, что сервер возвращает данные пользователя
      setLoading(false);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setError('You are not logged in.');
      } else {
        setError('An error occurred while loading profile data.');
      }
      setLoading(false);
    }
  };

  // Функция для выхода из системы
  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true }); // Отправляем запрос на выход
      navigate('/'); // Перенаправляем на главную страницу
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  useEffect(() => {
    fetchUserProfile();
  }, []);

  if (loading) {
    return <p>Loading profile...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p>Name: {currentUser.name}</p>
      <p>ID: {currentUser.id}</p>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Profile;
