import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import AuthPage from './pages/AuthPage';
import NotFound from './pages/NotFound';
import Detail from './pages/Detail';
import Layout from './components/Layout';
import Profile from './pages/Profile';
import Home from './pages/Home';
import './css/App.css';

// Компонент для защищенных маршрутов
const ProtectedRoute = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(null);
  const [loading, setLoading] = useState(true);

  const checkAuthentication = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/protected', {
        withCredentials: true, // Включаем отправку куки
      });
      setIsAuthenticated(!!response.data); // Если данные есть, пользователь авторизован
    } catch (error) {
      setIsAuthenticated(false); // Пользователь не авторизован
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    checkAuthentication();
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Индикатор загрузки
  }

  return isAuthenticated ? children : <Navigate to="/auth" replace />;
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Функция для обновления состояния аутентификации
  const updateAuthState = (status) => {
    setIsAuthenticated(status);
  };

  return (
    <Routes>
      {/* Страница авторизации */}
      <Route
        path="/auth"
        element={<AuthPage updateAuthState={updateAuthState} />}
      />

      {/* Защищенные маршруты */}
      <Route
        path="/"
        element={
          <ProtectedRoute>
            <Layout />
          </ProtectedRoute>
        }
      >
        <Route index element={<Navigate to="/home" />} />
        <Route path="home" element={<Home />} />
        <Route path="profile" element={<Profile />} />
        <Route path="detail" element={<Detail />} />
      </Route>

      {/* 404 страница */}
      <Route path="*" element={<NotFound />} />
    </Routes>
  );
}

export default App;
