import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/Home.css"; // Make sure CSS reflects removal of login form if needed

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Load device data - assumes user is authenticated because ProtectedRoute allowed access
  useEffect(() => {
    const loadData = async () => {
      setLoading(true); // Start loading
      setError(""); // Clear previous errors

      try {
        const response = await axios.get("http://localhost:5000/api/items", {
          withCredentials: true, // Send cookies to get user-specific items
        });
        setData(response.data);
      } catch (err) {
        // Handle errors fetching data (e.g., network error, server error)
        // A 401 here might mean the session expired *after* initial load but *before* this request completed
        if (err.response && err.response.status === 401) {
          setError("Your session may have expired. Please log out and log back in.");
          // Optionally trigger logout or redirect to login here
        } else if (err.response) {
          setError(err.response.data.message || "An error occurred while loading data.");
        } else if (err.request) {
          setError("No response from the server. Please try again later.");
        } else {
          setError("An unexpected error occurred.");
        }
        console.error("Error loading data:", err); // Log error for debugging
      } finally {
        setLoading(false); // Finish loading regardless of success/failure
      }
    };

    loadData();
  }, []); // Run once on mount

  // --- Rendering Logic ---
  // No login form needed here. ProtectedRoute handles unauthorized access.

  return (
    <div className="home-container">
      <div className="home-header">
        <h1 className="home-title">Device List</h1>

        {/* Add Device button */}
        <Link to="/add" className="add-device-button">
          Add Device
        </Link>
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Loading devices...</p>
        </div>
      ) : error ? (
        <div className="error-state">
          {/* Provide a way to retry or acknowledge error */}
          <p>{error}</p>
          {/* Optionally add a retry button */}
        </div>
      ) : data.length === 0 ? (
        <div className="empty-state">
          <p>No devices found.</p>
          {/* <p>No devices found. Add your first device!</p> */}
        </div>
      ) : (
        <div className="device-list">
          {data.map((item) => (
            // Ensure item.id is unique and stable
            <Link
              to={`/detail/${item.id}`} // Make sure Detail page route exists and is protected
              className="device-card"
              key={item.id}
            >
              {/* ... rest of the card content ... */}
              <div className="device-icon">
                <span className="icon-circle">&#9679;</span>
              </div>
              <div className="device-info">
                <h3>{item.name}</h3>
                <p className="device-type">{item.type || 'Unknown type'}</p>
                <p className="device-status">
                  <span
                    className={`status-indicator ${
                      item.status === 'Active' ? 'active' : 'inactive'
                    }`}
                  >
                    {item.status || 'Inactive'}
                  </span>
                </p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
