// Detail.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import '../css/Detail.css'; // Ensure this CSS file exists and is correctly linked

const Detail = () => {
    // Get the dynamic 'id' parameter from the URL
    const { id } = useParams();
    const navigate = useNavigate();

    // State for device details and editing mode
    const [name, setName] = useState('');
    const [location, setLocation] = useState('');
    const [deviceData, setDeviceData] = useState(null); // Store the full device object if needed
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isEditing, setIsEditing] = useState(false); // Editing flag

    useEffect(() => {
        const fetchDeviceData = async () => {
            setIsLoading(true);
            setError(null); // Reset error on new fetch
            try {
                // *** FIX: Added '/api' prefix to the URL ***
                const response = await axios.get(`http://localhost:5000/api/items/${id}`, {
                    withCredentials: true // Include credentials if endpoint becomes protected
                });
                // Store fetched data
                setDeviceData(response.data);
                setName(response.data.name);
                setLocation(response.data.location);
            } catch (err) {
                console.error("Error fetching device info: ", err);
                setError("Failed to load device data. Please try again later.");
                // Optionally navigate away or show specific error based on err.response.status
                if (err.response && err.response.status === 404) {
                    setError(`Device with ID ${id} not found.`);
                }
            } finally {
                setIsLoading(false);
            }
        };

        if (id) { // Only fetch if ID exists
            fetchDeviceData();
        } else {
            setError("No device ID provided.");
            setIsLoading(false);
        }

    }, [id]); // Re-run effect if the 'id' parameter changes

    const handleDelete = async () => {
        // Confirmation dialog
        if (window.confirm(`Are you sure you want to delete the device "${name}"?`)) {
            try {
                // *** FIX: Added '/api' prefix to the URL ***
                await axios.delete(`http://localhost:5000/api/items/${id}`, {
                    withCredentials: true // Include credentials if endpoint becomes protected
                });
                // *** FIX: Navigate to '/home' for consistency with App.js redirection ***
                navigate("/home", { replace: true }); // Go back to the home page after deletion

            } catch (err) {
                console.error("Error deleting this device: ", err);
                setError("Failed to delete device. Please try again.");
                // Optionally show a more specific error message
            }
        }
    };

    const handleSave = async () => {
        try {
            // *** FIX: Added '/api' prefix to the URL ***
            const updatedData = { ...deviceData, name, location }; // Send updated fields
            await axios.put(`http://localhost:5000/api/items/${id}`, updatedData, {
                withCredentials: true // Include credentials if endpoint becomes protected
            });
            setDeviceData(updatedData); // Update local full data state
            setIsEditing(false); // Exit editing mode
            setError(null); // Clear any previous errors
        } catch (err) {
            console.error("Error updating device info: ", err);
            setError("Failed to save changes. Please try again.");
            // Optionally show a more specific error message
        }
    };

    // Render loading state
    if (isLoading) {
        return <p>Loading device details...</p>;
    }

    // Render error state
    if (error) {
        return (
            <div className="detail-container error-message">
                <p>{error}</p>
                <button className="detail-button" onClick={() => navigate("/home")}>
                    Back to Home
                </button>
            </div>
        );
    }

    // Render device details and edit form
    return (
        <div className="detail-container">
            <h1 className="detail-title">Device Detailed Info</h1>
            {!isEditing ? (
                 // Display Mode
                <>
                    <div className="detail-info">
                        <p className="detail-field">
                            <span className="detail-label">ID:</span>
                            <strong>{deviceData?.id}</strong>
                        </p>
                        <p className="detail-field">
                            <span className="detail-label">Device Name:</span>
                            <strong>{name}</strong>
                        </p>
                         <p className="detail-field">
                            <span className="detail-label">Type:</span>
                            <strong>{deviceData?.type}</strong> {/* Display Type if available */}
                        </p>
                        <p className="detail-field">
                            <span className="detail-label">Location:</span>
                            <strong>{location}</strong>
                        </p>
                        <p className="detail-field">
                            <span className="detail-label">Status:</span>
                            <strong>{deviceData?.status}</strong> {/* Display Status if available */}
                        </p>
                    </div>

                    <div className="detail-actions">
                         {/* *** FIX: Navigate to '/home' *** */}
                        <button className="detail-button" onClick={() => navigate("/home")}>
                            Back to Home
                        </button>
                        <button className="detail-button edit" onClick={() => setIsEditing(true)}>
                            Edit
                        </button>
                        <button className="detail-button danger" onClick={handleDelete}>
                            Delete Device
                        </button>
                    </div>
                </>
            ) : (
                // Editing Mode
                <div className="edit-form">
                    <h3 className="edit-title">Edit Device Info</h3>
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            handleSave();
                        }}
                        className="edit-form-container"
                    >
                         <p className="detail-field">
                            <span className="detail-label">ID:</span>
                            <strong>{deviceData?.id}</strong> {/* ID is not editable */}
                        </p>
                        <label className="edit-label">
                            Device Name:
                            <input
                                type="text"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                                className="edit-input"
                            />
                        </label>
                        <br />
                        <label className="edit-label">
                            Location:
                            <input
                                type="text"
                                value={location}
                                onChange={(e) => setLocation(e.target.value)}
                                required
                                className="edit-input"
                            />
                        </label>
                        <br />
                        {/* Display other fields as non-editable if needed */}
                         <p className="detail-field read-only">
                            <span className="detail-label">Type:</span>
                            <strong>{deviceData?.type}</strong>
                        </p>
                         <p className="detail-field read-only">
                            <span className="detail-label">Status:</span>
                            <strong>{deviceData?.status}</strong>
                        </p>

                        <div className="edit-actions">
                            <button type="submit" className="edit-button save">
                                Save Changes
                            </button>
                            <button
                                type="button"
                                onClick={() => {
                                    // Reset changes on cancel
                                    setName(deviceData?.name || '');
                                    setLocation(deviceData?.location || '');
                                    setIsEditing(false);
                                    setError(null); // Clear errors on cancel
                                }}
                                className="edit-button cancel"
                            >
                                Cancel
                            </button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default Detail;
